mapper.select:
select
  name as clientName, certf_id as idCard, sex, home_address as address, telephone as tel, email, birthday, type as clientType
from client

    and name like CONCAT('%', #{name} ,'%')
    and certf_id = #{certf_id}
    and home_address like CONCAT('%', #{home_address} ,'%')
    and telephone = #{telephone}
    and email = #{email}
