mapper.update:
update client
name=#{baseInfo.name}, law_identity=#{baseInfo.identityEnum}, type=#{baseInfo.typeEnum}, nation=#{baseInfo.nationEnum}, certf_type=#{baseInfo.certfTypeEnum}, certf_id=#{baseInfo.certfId}, birthday=#{baseInfo.birthday}, sex=#{baseInfo.sexEnum}, married=#{baseInfo.marriedEnum}, telephone=#{baseInfo.telephone}, email=#{baseInfo.email}, backup=#{baseInfo.backup}, home_address=#{baseInfo.homeAddress}, home_code=#{baseInfo.homeCode}, other=#{baseInfo.other}, first_contact_way=#{baseInfo.firstContactWayEnum}, com_name=#{unitInfo.comName}, com_phone=#{unitInfo.comPhone}, fax_no=#{unitInfo.faxNo}, com_representative=#{unitInfo.representative}, com_tax_no=#{unitInfo.taxNo}, com_address=#{unitInfo.comAddress}, job_name=#{unitInfo.jobName}, job_content=#{unitInfo.jobContent}, job_type=#{unitInfo.jobType}, income=#{unitInfo.income},
where certf_id=#{baseInfo.certfId}

mapper.select:
select
  name as clientName, certf_id as idCard, sex, home_address as address, telephone as tel, email, date_format(birthday, '%Y-%m-%d') as birthday, type as clientType
from client

    and name like CONCAT('%', #{name} ,'%')
    and certf_id = #{certf_id}
    and home_address like CONCAT('%', #{home_address} ,'%')
    and telephone = #{telephone}
    and email = #{email}

        and type in

            #{clientType}



        and sex in

            #{sex}
