mapper.select:
select chinese_name,employ_no,branch_name,current_rank,department,state,contract_starttime,contract_endtime from employee_basic_information,employee_unit_rank,car_policy_contract,car_insurance_policy,car_insurance_type,car_insur_policy_mid ,team_record where employee_basic_information.employee_no=employee_unit_rank.employ_no and employee_unit_rank.state=#{state} and branchs in
#{branchs}
and employee_unit_rank.department=#{dept} and employee_unit_rank.current_rank=#{rank} and car_policy_contract.employee_no=employee_basic_information.employee_no and team_record.member_id=employee_basic_information.employee_no and car_policy_contract.team_no=team_record.team_id
and car_policy_contract.policy_no=car_insurance_policy.policy_no and car_insurance_policy.policy_no= car_insur_policy_mid.policy_no and car_insur_policy_mid.car_insur_id=car_insurance_type.car_insur_id and car_insurance_policy.pay_date_car <= DATE_ADD(#{baseMonth},
INTERVAL 1 MONTH
INTERVAL 3 MONTH
INTERVAL 6 MONTH
INTERVAL 1 YEAR
0 SECONDS
) group by employee_basic_information.employee_no having SUM(premium*(commission_rate_out+commission_rate_in)) ${condition} #{conditionValue}

select chinese_name,employ_no,branch_name,current_rank,department,state,contract_starttime,contract_endtime from employee_basic_information,employee_unit_rank,life_policy_contract,life_insurance_policy,insurance_type ,team_record where employee_basic_information.employee_no=employee_unit_rank.employ_no and employee_unit_rank.state=#{state} and branchs in
#{branchs}
and employee_unit_rank.department=#{dept} and employee_unit_rank.current_rank=#{rank} and team_record.member_id=employee_basic_information.employee_no and team_record.team_id=life_policy_contract.team_no
and life_policy_contract.employee_no=employee_basic_information.employee_no and life_policy_contract.policy_no=life_insurance_policy.policy_no and life_insurance_policy.insur_id= insurance_type.insur_id and life_insurance_policy.valid_date_since <= DATE_ADD(#{baseMonth},
1 MONTHS
3 MONTHS
6 MONTHS
1 YEARS
0 SECONDS
) group by employee_basic_information.employee_no having SUM(CONVERT(SUBSTRING(insurance_type.period,3),SIGNED)/CONVERT(LEFT(insurance_type.period,2),SIGNED)*premium) ${condition} #{conditionValue}
